"use client";

import { FiSun, FiMoon } from "react-icons/fi";
import { useState, useEffect } from "react";
import { useTheme } from "next-themes";
import Image from "next/image";

export default function ThemeSwitch() {
  const [mounted, setMounted] = useState(false);
  const { setTheme, resolvedTheme } = useTheme();

  useEffect(() => setMounted(true), []);

  if (!mounted)
    return (
      <Image
        src="data:image/svg+xml;base64,"
        width={36}
        height={36}
        sizes="36x36"
        alt="Loading Light/Dark Toggle"
        priority={false}
        title="Loading Light/Dark Toggle"
      />
    );
  if (resolvedTheme === "dark") {
    return (
      <div
        onClick={() => setTheme("light")}
        className="flex items-center justify-center w-6 h-6 text-white rounded-full bg-dark "
      >
        {" "}
        <FiSun className="rounded-full " />
      </div>
    );
  }

  if (resolvedTheme === "light") {
    return (
      <div
        onClick={() => setTheme("dark")}
        className="flex items-center justify-center w-6 h-6 bg-white rounded-full text-dark"
      >
        {" "}
        <FiMoon className="rounded-full " />
      </div>
    );
  }
}
